/** @format */
import classes from "./Checkout.module.css";
import { useRef, useState } from "react";
const isEmpty = value => value.trim() === "";
const isFiveChars = value => value.length === 5;
const Checkout = props => {
  const [formInputValidity, setFormInputValidity] = useState({
    name: true,
    address: true,
    postalCode: true,
    city: true,
  });
  //
  const name = useRef();
  const address = useRef();
  const postalCode = useRef();
  const city = useRef();
  //

  const nameIsValid = !isEmpty(name);
  const addressIsValid = !isEmpty(address);
  const cityIsValid = !isEmpty(city);
  const postalCodeIsValid = !isFiveChars(postalCode);

  //
  setFormInputValidity({
    name: nameIsValid,
  });
  const formIsValid =
    nameIsValid && addressIsValid && cityIsValid && postalCodeIsValid;

  if (!formIsValid) {
    return;
  }
  const confirmHandler = event => {
    event.preventDefault();
    console.log(event);
  };
  return (
    <form onSubmit={confirmHandler}>
      <div className={classes.control}>
        <label htmlFor="name">Your Name</label>
        <input type="text" id="name" ref={name}>
          {" "}
        </input>
      </div>
      <div className={classes.control}>
        <label htmlFor="address">Address</label>
        <input type="text" id="address" ref={address}></input>
      </div>
      <div className={classes.control}>
        <label htmlFor="postalcode">Postal Code</label>
        <input type="text" id="postal-code" ref={postalCode}></input>
      </div>
      <div className={classes.control}>
        <label htmlFor="city">City</label>
        <input type="text" id="city" ref={city}></input>
      </div>
      <div className={classes.actions}>
        <button type="button" onClick={props.onClose}>
          Cancel
        </button>
        <button className={classes.submit}>Confirm</button>
      </div>
    </form>
  );
};
export default Checkout;
